<?phprequire_once("../config/db.php");require '..\PHPMailer-master\PHPMailerAutoload.php';session_start();function approve() {    $db_connection = new mysqli(DB_HOST, DB_USER, DB_PASS, DB_NAME);    $current = $db_connection->query("SELECT * FROM timesheet_info         WHERE emp_id= '". $_POST['approve_btn'] ."' ORDER BY id DESC LIMIT 1;");    $obj = $current->fetch_object();    $res = $db_connection->query("UPDATE timesheet_info SET state='". "APPROVE" ."'         WHERE emp_id= '". $_POST['approve_btn'] ."' and month='". $obj->month ."' and year='". $obj->year ."';");    $month = $obj->month + 1;    $year = $obj->year;    if($month > 12)    {        $month = 1;        $year++;    }    echo $_POST['approve_btn'] . " " . $month . " " . $year;    $db_connection->query("INSERT INTO timesheet_info (emp_id, month, year, state)         VALUES( '" . $_POST['approve_btn'] . "', '" . $month . "', '" . $year . "', '"."OPEN"."') ;");    }function reject() {    $db_connection = new mysqli(DB_HOST, DB_USER, DB_PASS, DB_NAME);    $current = $db_connection->query("SELECT * FROM timesheet_info         WHERE emp_id= '". $_POST['reject_btn'] ."' ORDER BY id DESC LIMIT 1;");    $obj = $current->fetch_object();    $res = $db_connection->query("UPDATE timesheet_info SET state='". "REJECT" ."'         WHERE emp_id= '". $_POST['reject_btn'] ."' and month='". $obj->month ."' and year='". $obj->year ."';");        $res = $db_connection->query("SELECT * FROM employee         WHERE id= '". $_POST['reject_btn'] ."';");    $obj = $res->fetch_object();        $mail = new PHPMailer();	$mail->isSMTP();                    // Set mailer to use SMTP	$mail->Host = 'smtp.gmail.com';     // Specify main and backup server	$mail->Port=587;	$mail->SMTPAuth = true;             // Enable SMTP authentication	$mail->Username = 'blaravirobla@gmail.com';// SMTP username	$mail->Password = 'passwordraviro';// SMTP password	$mail->SMTPSecure = 'tls';         // Enable encryption, 'ssl' also accepted	$mail->From = $_SESSION['user_email'];	$mail->FromName = $_SESSION['numeprenume'];		$mail->addAddress('necula.roxana@yahoo.com', 'Necula Roxana');	$mail->addAddress('straticiuc_vicu@yahoo.com', 'Straticiuc Vicu');	$mail->addAddress('rares1991petrescu@yahoo.com', 'Petrescu Rares');	$mail->addAddress($obj->email, $obj->numeprenume);	$mail->WordWrap = 50;                                 // Set word wrap to 50 characters	//$mail->addAttachment('/var/tmp/file.tar.gz');         // Add attachments	//$mail->addAttachment('/tmp/image.jpg', 'new.jpg');    // Optional name	$mail->isHTML(true);                                  // Set email format to HTML	$mail->Subject = 'Raport rejectat';	$mail->Body    = 'Buna ziua! Aveti un raport rejectat. Cauza: '. $_POST['reject_info'] .'. Va dorim o zi frumoasa, Echipa Raviro';	$mail->AltBody = 'Buna ziua! Aveti un raport rejectat. Echipa Raviro';	if(!$mail->send()) {	   echo 'Mail could not be sent. \n';	   echo 'Mailer Error: ' . $mail->ErrorInfo;	   exit;	}	echo 'Mail has been sent\n';}if (isset($_POST["approve_btn"])) {    approve();}if (isset($_POST["reject_btn"])) {    reject();}header('Location: ../user_page.php');?>