<?phpsession_start();include("common.php");require_once("../config/db.php");function listEmps(){    $db_connection = new mysqli(DB_HOST, DB_USER, DB_PASS, DB_NAME);    $query_result = 0;    if( $_SESSION['role_name'] == "Director" )    {        $query_result = $db_connection->query("SELECT emp.id emp_id, emp.numeprenume emp_name, role.name role_name, dept.name dept_name FROM employee emp                                             left join role on role.id = emp.role_id                                             left join department dept on emp.dept_id = dept.id;");    }    else if( $_SESSION['role_name'] == "Sef departament" )    {        $query_result = $db_connection->query("SELECT emp.id emp_id, emp.numeprenume emp_name, role.name role_name, dept.name dept_name FROM employee emp                                             left join role on role.id = emp.role_id                                             left join department dept on emp.dept_id = dept.id                                            WHERE emp.dept_id='".$_SESSION['dept_id']."' and emp.role_id != 2;");    }    else if( $_SESSION['role_name'] == "Sef divizie" )    {        $result = $db_connection->query("SELECT * FROM department WHERE id = '".$_SESSION['dept_id']."' ;");        $obj = $result->fetch_object();        $query_result = $db_connection->query("SELECT emp.id emp_id, emp.numeprenume emp_name, role.name role_name, dept.name dept_name FROM employee emp                                             left join role on role.id = emp.role_id                                             left join department dept on emp.dept_id = dept.id                                            left join divizion divi on dept.division_id = divi.id                                            WHERE divi.id = '".$obj->division_id."' and emp.role_id != 5 and emp.role_id != 4 and emp.role_id != 3;");    }            $r = '';    $r .= '<table id="testTable" border="2">';    $r .= '    <tr style="background-color:#ccc;">';    $r .= '         <td width="200" align="center">Departament</td>';    $r .= '         <td width="150" align="center">Functie</td>';    $r .= '         <td width="200" align="center">Angajat</td>';    $r .= '         <td width="20" align="center"></td>';    $r .= '    </tr>';    while( $query_result && $obj = $query_result->fetch_object() )    {        $r .= ' <tr>';        $r .= '     <td align="center">'. $obj->dept_name .'</td>';        $r .= '     <td align="center">'. $obj->role_name .'</td>';        $r .= '     <td align="center">'. $obj->emp_name .'</td>';        $r .= '     <td align="center"><button name="show_btn" type="submit" value="'.$obj->emp_id.'">Arata</button></td>';        $r .= ' </tr>';    }    $r .= '</table>';    return $r;}?><form method="post" action="../classes/verify_timesheet.php" name="raportform">    <table>        <tr>            <td width = "600">                <?php echo listEmps(); ?>            </td>    </table></form> 